import React from 'react';
import styled from 'styled-components';
import { useFilterContext } from '../context/filter_context';
import { getUniqueValues, formatPrice } from '../utils/helpers';
import { FaCheck } from 'react-icons/fa';

const Filters = () => {
  const {
    handleFilterChange,
    filterValue,
    products,
    clearFilters,
  } = useFilterContext();
  const categories = getUniqueValues(products, 'category');
  const companies = getUniqueValues(products, 'company');
  const colors = getUniqueValues(products, 'colors');
  console.log(filterValue.colors);
  return (
    <Wrapper>
      <div className='content'>
        <div className='form-control'>
          <input
            type='text'
            name='text'
            placeholder='search'
            className='search-input'
            value={filterValue.text}
            onChange={handleFilterChange}
          />

          <div className='form-control'>
            <h5>category</h5>
            <div>
              {categories.map((category) => {
                return (
                  <button
                    type='button'
                    name='category'
                    className={
                      category === filterValue.category ? 'active' : null
                    }
                    onClick={handleFilterChange}
                  >
                    {category}
                  </button>
                );
              })}
            </div>
          </div>
          <div className='form-control'>
            <h5>company</h5>
            <select
              name='company'
              className='company'
              onChange={handleFilterChange}
            >
              {companies.map((company) => {
                return <option value={company}>{company}</option>;
              })}
            </select>
          </div>
          <div className='form-control'>
            <h5>colors</h5>
            <div className='colors'>
              {colors.map((color) => {
                if (color === 'ALL') {
                  return (
                    <button
                      name='colors'
                      data-color={color}
                      className={
                        color === filterValue.colors
                          ? 'all-btn active'
                          : 'all-btn'
                      }
                      onClick={handleFilterChange}
                    >
                      {color}
                    </button>
                  );
                }
                return (
                  <button
                    name='colors'
                    className={
                      color === filterValue.colors
                        ? 'color-btn active'
                        : 'color-btn'
                    }
                    data-color={color}
                    style={{ background: color }}
                    onClick={handleFilterChange}
                  >
                    {color === filterValue.colors && <FaCheck />}
                  </button>
                );
              })}
            </div>
          </div>
          <div className='form-control shipping' onChange={handleFilterChange}>
            <label for='shipping'>free shipping</label>
            <input type='checkbox' name='shipping' id='shipping' />
          </div>
          <div className='form-control'>
            <h5>price</h5>
            <p class='price'>{formatPrice(filterValue.price)}</p>
            <input
              type='range'
              name='price'
              min={filterValue.minPrice}
              max={filterValue.maxPrice}
              value={filterValue.price}
              onChange={handleFilterChange}
            />
          </div>
        </div>
        <button type='button' className='clear-btn' onClick={clearFilters}>
          clear filters
        </button>
      </div>
    </Wrapper>
  );
};

const Wrapper = styled.section`
  .form-control {
    margin-bottom: 1.25rem;
    h5 {
      margin-bottom: 0.5rem;
    }
  }
  .search-input {
    padding: 0.5rem;
    background: var(--clr-grey-10);
    border-radius: var(--radius);
    border-color: transparent;
    letter-spacing: var(--spacing);
  }
  .search-input::placeholder {
    text-transform: capitalize;
  }

  button {
    display: block;
    margin: 0.25em 0;
    padding: 0.25rem 0;
    text-transform: capitalize;
    background: transparent;
    border: none;
    border-bottom: 1px solid transparent;
    letter-spacing: var(--spacing);
    color: var(--clr-grey-5);
    cursor: pointer;
  }
  .active {
    border-color: var(--clr-grey-5);
  }
  .company {
    background: var(--clr-grey-10);
    border-radius: var(--radius);
    border-color: transparent;
    padding: 0.25rem;
  }
  .colors {
    display: flex;
    align-items: center;
  }
  .color-btn {
    display: inline-block;
    width: 1rem;
    height: 1rem;
    border-radius: 50%;
    background: #222;
    margin-right: 0.5rem;
    border: none;
    cursor: pointer;
    opacity: 0.5;
    display: flex;
    align-items: center;
    justify-content: center;
    svg {
      font-size: 0.5rem;
      color: var(--clr-white);
    }
  }
  .all-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 0.5rem;
    opacity: 0.5;
  }
  .active {
    opacity: 1;
  }
  .all-btn .active {
    text-decoration: underline;
  }
  .price {
    margin-bottom: 0.25rem;
  }
  .shipping {
    display: grid;
    grid-template-columns: auto 1fr;
    align-items: center;
    text-transform: capitalize;
    column-gap: 0.5rem;
    font-size: 1rem;
  }
  .clear-btn {
    background: var(--clr-red-dark);
    color: var(--clr-white);
    padding: 0.25rem 0.5rem;
    border-radius: var(--radius);
  }
  @media (min-width: 768px) {
    .content {
      position: sticky;
      top: 1rem;
    }
  }
`;

export default Filters;
